package JavaPgms;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//increment
		for (int i=1;i<=10;i++){
			System.out.println("selenium");
			System.out.println(i);
			
			
		}
		//decrement
		for(int n=10;n>=1;n--){
			System.out.println(n);
		}

	}

}
